import { createStore } from 'vuex';
export default createStore({
    state: {
        chatMessage:{}
    },
    getters: {
        getChatContent(state) {
           return  state.chatMessage;
        }
    },
    mutations: {
        setMySearchText(state, payload) {
            let { question, answer } = payload;
            console.log('question', question, answer);
            state.chatMessage[`${question}`] = answer;
        }

    }
})
