import { createStore } from 'vuex';
import axios from 'axios';
export default createStore({
    state: {
        userText: [],
        botText:[]
    },
    getters: {
        getChatContent(state) {
           return  state.chatText;
        },
        getBotText(state) {
            return state.botText;
        }
    },
    mutations: {
        setUserText(state, payload) {
            state.chatText.push(payload);
        },
        setBotText(state, payload) {
            state.chatText[state.chatText.length-1].push(payload);
        }
    },
    actions: {
        getRespose(context,payload) {
            try {
                let url = 'http://localhost:5000/'
                let result = axios.post(url, { body: payload }, {
                    headers: {
                        'Content-Type': 'application/json'
                    }
                }
                )
                console.log('result', result);
                context.commit('setCha')
            } catch (error) {
                console.warn(error);
            }
            
        }

    }
})
